mapper.select:
SELECT ( SELECT count(wm_order_id) FROM wm_order
WHERE DATE_FORMAT(created_time, '%Y-%m-%d') >= DATE_FORMAT(#{days}, '%Y-%m-%d')
) AS countOrder, ( SELECT count(wm_order_id) FROM wm_order WHERE `status` = 1
AND DATE_FORMAT(created_time, '%Y-%m-%d') >= DATE_FORMAT(#{days}, '%Y-%m-%d')
) AS successOrder, ( SELECT count(wm_order_id) FROM wm_order WHERE `status` = 0
AND DATE_FORMAT(created_time, '%Y-%m-%d') >= DATE_FORMAT(#{days}, '%Y-%m-%d')
) AS failOrder, ( SELECT sum(total_fee) FROM wm_order WHERE `status` = 1
AND DATE_FORMAT(created_time, '%Y-%m-%d') >= DATE_FORMAT(#{days}, '%Y-%m-%d')
) AS sumSuccessTotalFee, ( SELECT CASE WHEN sum(total_fee) IS NULL THEN 0 ELSE sum(total_fee) END FROM wm_order WHERE `status` = 0
AND DATE_FORMAT(created_time, '%Y-%m-%d') >= DATE_FORMAT(#{days}, '%Y-%m-%d')
) AS sumFailTotalFee
